import React, { useState } from 'react';
import './Lend.css';
import Modal from '../Modal/Modal'; // Import the Modal component

const API_URL = 'http://localhost:3001/api/v1';

function Lend() {
    const [formData, setFormData] = useState({
        customer_id: '',
        loan_amount: '',
        loan_period_years: '',
        interest_rate_yearly: ''
    });
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    // State for managing the modal
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalContent, setModalContent] = useState({ title: '', body: null });
    const [copyButtonText, setCopyButtonText] = useState('Copy');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleCopy = (text) => {
        navigator.clipboard.writeText(text).then(() => {
            setCopyButtonText('Copied!');
            setTimeout(() => setCopyButtonText('Copy'), 2000);
        }, (err) => {
            console.error('Failed to copy text: ', err);
            setCopyButtonText('Failed');
        });
    };
    
    // Custom close handler to reset the copy button text
    const closeModalAndReset = () => {
        setIsModalOpen(false);
        setCopyButtonText('Copy');
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);
        try {
            const response = await fetch(`${API_URL}/loans`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });
            const data = await response.json();
            
            if (!response.ok) {
                // Check for the specific "customer not found" error from the backend
                if (data.error && data.error.includes('Customer not found')) {
                    setModalContent({
                        title: "Customer Not Found",
                        body: <p>The Customer ID "{formData.customer_id}" does not exist. Please verify the ID or add the customer first.</p>
                    });
                    setIsModalOpen(true);
                } else {
                    // Handle all other errors generically
                    throw new Error(data.error || 'Failed to create loan.');
                }
            } else {
                // Handle success case
                setModalContent({
                    title: "Loan Created Successfully",
                    body: (
                        <>
                            <p>{`A new loan has been originated for ${data.customer_name}.`}</p>
                            <div className="modal-details">
                                <div className="modal-detail-item">
                                    <span className="modal-detail-label">Loan ID:</span>
                                    <div className="modal-value-container">
                                        <span className="modal-detail-value">{data.loan_id}</span>
                                        <button className={`copy-button ${copyButtonText === 'Copied!' ? 'copied' : ''}`} onClick={() => handleCopy(data.loan_id)}>
                                            {copyButtonText}
                                        </button>
                                    </div>
                                </div>
                                <div className="modal-detail-item">
                                    <span className="modal-detail-label">Total Payable:</span>
                                    <span className="modal-detail-value">{`₹${data.total_amount_payable}`}</span>
                                </div>
                                <div className="modal-detail-item">
                                    <span className="modal-detail-label">Monthly EMI:</span>
                                    <span className="modal-detail-value">{`₹${data.monthly_emi}`}</span>
                                </div>
                            </div>
                        </>
                    )
                });
                setIsModalOpen(true);
                setFormData({ customer_id: '', loan_amount: '', loan_period_years: '', interest_rate_yearly: '' });
            }
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <>
            <Modal 
                isOpen={isModalOpen} 
                onClose={closeModalAndReset} 
                title={modalContent.title}
            >
                {modalContent.body}
            </Modal>

            <h2 className="component-title">Create a New Loan</h2>
            <p className="component-subtitle">Issue a new loan to an existing customer.</p>

            <form onSubmit={handleSubmit} className="form-container">
                <input 
                    type="text" 
                    name="customer_id" 
                    value={formData.customer_id} 
                    onChange={handleChange} 
                    placeholder="Enter Customer ID" 
                    required 
                    disabled={isLoading}
                />
                <input 
                    type="number" 
                    name="loan_amount" 
                    value={formData.loan_amount} 
                    onChange={handleChange} 
                    placeholder="Loan Amount (₹)" 
                    required 
                    disabled={isLoading}
                />
                <input 
                    type="number" 
                    name="loan_period_years" 
                    value={formData.loan_period_years} 
                    onChange={handleChange} 
                    placeholder="Loan Period (Years)" 
                    required 
                    disabled={isLoading}
                />
                <input 
                    type="number" 
                    name="interest_rate_yearly" 
                    value={formData.interest_rate_yearly} 
                    onChange={handleChange} 
                    placeholder="Interest Rate (% Yearly)" 
                    required 
                    disabled={isLoading}
                />
                <button type="submit" disabled={isLoading}>
                    {isLoading ? 'Creating...' : 'Create Loan'}
                </button>
            </form>

            {error && <p className="error">{error}</p>}
        </>
    );
}

export default Lend;
