import React, { useState } from 'react';
import './Payment.css';

const API_URL = 'http://localhost:3001/api/v1';

function Payment() {
    const [formData, setFormData] = useState({
        loan_id: '',
        amount: '',
        payment_type: 'EMI'
    });
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setResult(null);
        try {
            const response = await fetch(`${API_URL}/loans/${formData.loan_id}/payments`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ amount: formData.amount, payment_type: formData.payment_type })
            });
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to record payment.');
            setResult(data);
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="payment-container">
            <h2>Record a Payment</h2>
            <form onSubmit={handleSubmit} className="form-container">
                <input type="text" name="loan_id" value={formData.loan_id} onChange={handleChange} placeholder="Loan ID" required />
                <input type="number" name="amount" value={formData.amount} onChange={handleChange} placeholder="Payment Amount" required />
                <select name="payment_type" value={formData.payment_type} onChange={handleChange}>
                    <option value="EMI">EMI</option>
                    <option value="LUMP_SUM">Lump Sum</option>
                </select>
                <button type="submit">Record Payment</button>
            </form>
            {error && <p className="error">{error}</p>}
            {result && (
                <div className="result">
                    <h3>Payment Recorded</h3>
                    <pre>{JSON.stringify(result, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}

export default Payment;
