import React, { useState } from 'react';
import './AddCustomer.css';

const API_URL = 'http://localhost:3001/api/v1';

function AddCustomer() {
    const [formData, setFormData] = useState({
        customer_id: '',
        name: ''
    });
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setResult(null);
        try {
            const response = await fetch(`${API_URL}/customers`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to add customer.');
            setResult(data);
            setFormData({ customer_id: '', name: '' }); // Clear form on success
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="add-customer-container">
            <h2>Add New Customer</h2>
            <p>Create a new customer profile in the system.</p>
            <form onSubmit={handleSubmit} className="form-container">
                <input type="text" name="customer_id" value={formData.customer_id} onChange={handleChange} placeholder="Enter a unique Customer ID (e.g., customer_002)" required />
                <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Enter Customer's Full Name" required />
                <button type="submit">Add Customer</button>
            </form>
            {error && <p className="error">{error}</p>}
            {result && (
                <div className="result">
                    <h3>{result.message}</h3>
                    <p><strong>Customer ID:</strong> {result.customer_id}</p>
                    <p><strong>Name:</strong> {result.name}</p>
                </div>
            )}
        </div>
    );
}

export default AddCustomer;
