import React, { useState } from 'react';
import './Lend.css';

const API_URL = 'http://localhost:3001/api/v1';

function Lend() {
    const [formData, setFormData] = useState({
        customer_id: 'customer_001',
        loan_amount: '',
        loan_period_years: '',
        interest_rate_yearly: ''
    });
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setResult(null);
        try {
            const response = await fetch(`${API_URL}/loans`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to create loan.');
            setResult(data);
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="lend-container">
            <h2>Create a New Loan</h2>
            <form onSubmit={handleSubmit} className="form-container">
                <input type="text" name="customer_id" value={formData.customer_id} onChange={handleChange} placeholder="Customer ID" required />
                <input type="number" name="loan_amount" value={formData.loan_amount} onChange={handleChange} placeholder="Loan Amount (₹)" required />
                <input type="number" name="loan_period_years" value={formData.loan_period_years} onChange={handleChange} placeholder="Loan Period (Years)" required />
                <input type="number" name="interest_rate_yearly" value={formData.interest_rate_yearly} onChange={handleChange} placeholder="Interest Rate (% Yearly)" required />
                <button type="submit">Create Loan</button>
            </form>
            {error && <p className="error">{error}</p>}
            {result && (
                <div className="result">
                    <h3>Loan Created Successfully</h3>
                    <p><strong>Customer:</strong> {result.customer_name} ({result.customer_id})</p>
                    <p><strong>Loan ID:</strong> {result.loan_id}</p>
                    <p><strong>Total Amount Payable:</strong> ₹{result.total_amount_payable}</p>
                    <p><strong>Monthly EMI:</strong> ₹{result.monthly_emi}</p>
                </div>
            )}
        </div>
    );
}

export default Lend;
