import React, { useState } from 'react';
import './Payment.css';
import Modal from '../Modal/Modal'; // Import the new Modal component

const API_URL = 'http://localhost:3001/api/v1';

function Payment() {
    const [formData, setFormData] = useState({
        loan_id: '',
        amount: '',
        payment_type: 'EMI'
    });
    const [error, setError] = useState('');
    
    // State for managing the modal
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalInfo, setModalInfo] = useState({ title: '', message: '' });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };
    
    const handleShowModal = (title, message) => {
        setModalInfo({ title, message });
        setIsModalOpen(true);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        
        try {
            if (!formData.loan_id.trim()) throw new Error('Loan ID is required.');
            
            const response = await fetch(`${API_URL}/loans/${formData.loan_id}/payments`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    amount: formData.amount,
                    payment_type: formData.payment_type
                })
            });
            const data = await response.json();
            
            if (!response.ok) throw new Error(data.error || 'Failed to record payment.');
            
            // Logic to determine which modal to show
            if (data.loan_closed) {
                if (parseFloat(data.overpaid_amount) > 0) {
                    handleShowModal(
                        "Loan Closed with Overpayment",
                        `The loan is now fully paid. You have overpaid by ₹${data.overpaid_amount}. This amount can be refunded.`
                    );
                } else {
                    handleShowModal(
                        "Loan Closed!",
                        `Congratulations! The loan with ID ${data.loan_id} has been fully paid off.`
                    );
                }
            } else {
                 handleShowModal(
                    "Payment Successful",
                    `Your payment of ₹${formData.amount} was successful. The new balance is ₹${data.remaining_balance}.`
                 );
            }
            // Clear form after successful payment
            setFormData({ loan_id: '', amount: '', payment_type: 'EMI' });

        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="payment-container">
            {/* The Modal component is rendered here */}
            <Modal 
                isOpen={isModalOpen} 
                onClose={() => setIsModalOpen(false)} 
                title={modalInfo.title}
            >
                <p>{modalInfo.message}</p>
            </Modal>

            <h2>Record a Payment</h2>
            <form onSubmit={handleSubmit} className="form-container">
                <input type="text" name="loan_id" value={formData.loan_id} onChange={handleChange} placeholder="Loan ID" required />
                <input type="number" name="amount" value={formData.amount} onChange={handleChange} placeholder="Payment Amount (₹)" required />
                <select name="payment_type" value={formData.payment_type} onChange={handleChange}>
                    <option value="EMI">EMI</option>
                    <option value="LUMP_SUM">Lump Sum</option>
                </select>
                <button type="submit">Record Payment</button>
            </form>
            {error && <p className="error">{error}</p>}
        </div>
    );
}

export default Payment;
