import React, { useState } from 'react';
import './Ledger.css';

const API_URL = 'http://localhost:3001/api/v1';

function Ledger() {
    const [loanId, setLoanId] = useState('');
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');

    const handleFetch = async (e) => {
        e.preventDefault();
        setError('');
        setResult(null);
        if (!loanId) return;

        try {
            const response = await fetch(`${API_URL}/loans/${loanId}/ledger`);
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to fetch ledger.');
            setResult(data);
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="ledger-container">
            <h2>View Loan Ledger</h2>
            <form onSubmit={handleFetch} className="form-container">
                <input type="text" value={loanId} onChange={(e) => setLoanId(e.target.value)} placeholder="Enter Loan ID" required />
                <button type="submit">Get Ledger</button>
            </form>
            {error && <p className="error">{error}</p>}
            {result && (
                <div className="result">
                    <h3>Ledger for Loan ID: {result.loan_id}</h3>
                    <p><strong>Principal:</strong> ₹{result.principal}</p>
                    <p><strong>Total Amount:</strong> ₹{result.total_amount}</p>
                    <p><strong>Monthly EMI:</strong> ₹{result.monthly_emi}</p>
                    <p><strong>Amount Paid:</strong> ₹{result.amount_paid}</p>
                    <p><strong>Balance Amount:</strong> ₹{result.balance_amount}</p>
                    <p><strong>EMIs Left:</strong> {result.emis_left}</p>
                    
                    <h4>Transactions</h4>
                    <table className="results-table">
                        <thead>
                            <tr>
                                <th>Transaction ID</th>
                                <th>Date</th>
                                <th>Amount (₹)</th>
                                <th>Type</th>
                            </tr>
                        </thead>
                        <tbody>
                            {result.transactions.map(tx => (
                                <tr key={tx.transaction_id}>
                                    <td>{tx.transaction_id}</td>
                                    <td>{new Date(tx.date).toLocaleString()}</td>
                                    <td>{tx.amount.toFixed(2)}</td>
                                    <td>{tx.type}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default Ledger;
