import React, { useState } from 'react';
import './Ledger.css';

const API_URL = 'http://localhost:3001/api/v1';

function Ledger() {
    const [loanId, setLoanId] = useState('');
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');

    const handleFetch = async (e) => {
        e.preventDefault();
        setError('');
        setResult(null);
        if (!loanId) return;

        try {
            const response = await fetch(`${API_URL}/loans/${loanId}/ledger`);
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to fetch ledger.');
            setResult(data);
        } catch (err) {
            setError(err.message);
        }
    };

    const formatStatus = (status) => {
        if (!status) return 'N/A';
        return status.replace(/_/g, ' ').toUpperCase();
    };

    const formatCurrency = (value) =>
        isNaN(value) ? 'N/A' : `â‚¹${parseFloat(value).toLocaleString('en-IN')}`;

    return (
        <div className="ledger-container">
            <h2>View Loan Ledger</h2>

            <form onSubmit={handleFetch} className="form-container">
                <input
                    type="text"
                    value={loanId}
                    onChange={(e) => setLoanId(e.target.value)}
                    placeholder="Enter Loan ID"
                    required
                />
                <button type="submit">Get Ledger</button>
            </form>

            {error && <p className="error">{error}</p>}

            {result && (
                <div className="ledger-results">
                    <div className="ledger-summary">
                        <h3>Loan Summary</h3>
                        <p><strong>Loan ID:</strong> {result.loan_id || 'N/A'}</p>
                        <p><strong>Customer:</strong> {result.customer_name || 'N/A'} ({result.customer_id || 'N/A'})</p>
                        <p><strong>Status:</strong> <span className={`status-${result.status}`}>{formatStatus(result.status)}</span></p>
                        <p><strong>Principal:</strong> {formatCurrency(result.principal)}</p>
                        <p><strong>Total Payable:</strong> {formatCurrency(result.total_amount)}</p>
                        <p><strong>Amount Paid:</strong> {formatCurrency(result.amount_paid)}</p>
                        <p><strong>Balance:</strong> {formatCurrency(result.balance_amount)}</p>
                        <p><strong>Monthly EMI:</strong> {formatCurrency(result.monthly_emi)}</p>
                        <p><strong>EMIs Left:</strong> {result.emis_left ?? 'N/A'}</p>
                    </div>

                    <div className="transactions-section">
                        <h3>Transactions</h3>
                        <div className="table-wrapper">
                            <table className="transactions-table">
                                <thead>
                                    <tr>
                                        <th>Transaction ID</th>
                                        <th>Date & Time (IST)</th>
                                        <th>Type</th>
                                        <th>Amount</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {Array.isArray(result.transactions) && result.transactions.length > 0 ? (
                                        result.transactions.map((tx) => (
                                            <tr key={tx.transaction_id}>
                                                <td data-label="Transaction ID">{tx.transaction_id}</td>
                                                <td data-label="Date">
                                                    {tx.date
                                                        ? new Date(tx.date).toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' })
                                                        : 'N/A'}
                                                </td>
                                                <td data-label="Type">{tx.type || 'N/A'}</td>
                                                <td data-label="Amount">{formatCurrency(tx.amount)}</td>
                                            </tr>
                                        ))
                                    ) : (
                                        <tr>
                                            <td colSpan="4">No transactions found for this loan.</td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Ledger;
