import React, { useState } from 'react';
import './Lend.css';

const API_URL = 'http://localhost:3001/api/v1';

function Lend() {
    const [formData, setFormData] = useState({
        customer_id: '',
        loan_amount: '',
        loan_period_years: '',
        interest_rate_yearly: ''
    });
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setResult(null);
        setLoading(true);
        try {
            const response = await fetch(`${API_URL}/loans`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to create loan.');
            
            setResult(data);

            // Clear the form fields after successful submission
            setFormData(prev => ({
                ...prev,
                loan_amount: '',
                loan_period_years: '',
                interest_rate_yearly: ''
            }));

        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="lend-container">
            <h2 className="component-title">Create a New Loan</h2>
            <p className="component-subtitle">Issue a new loan to an existing customer.</p>

            <form onSubmit={handleSubmit} className="form-container">
                <input 
                    type="text" 
                    name="customer_id" 
                    value={formData.customer_id} 
                    onChange={handleChange} 
                    placeholder="Enter Customer ID" 
                    required 
                    disabled={loading}
                />
                <input 
                    type="number" 
                    name="loan_amount" 
                    value={formData.loan_amount} 
                    onChange={handleChange} 
                    placeholder="Loan Amount (₹)" 
                    required 
                    disabled={loading}
                />
                <input 
                    type="number" 
                    name="loan_period_years" 
                    value={formData.loan_period_years} 
                    onChange={handleChange} 
                    placeholder="Loan Period (Years)" 
                    required 
                    disabled={loading}
                />
                <input 
                    type="number" 
                    name="interest_rate_yearly" 
                    value={formData.interest_rate_yearly} 
                    onChange={handleChange} 
                    placeholder="Interest Rate (% Yearly)" 
                    required 
                    disabled={loading}
                />
                <button type="submit" disabled={loading}>
                    {loading ? 'Creating...' : 'Create Loan'}
                </button>
            </form>

            {error && <p className="error">{error}</p>}
            
            {result && (
                <div className="result-feedback">
                    <h3>Loan Created Successfully</h3>
                    <p>
                        <strong>For:</strong> {result.customer_name} ({result.customer_id})<br/>
                        <strong>Loan ID:</strong> {result.loan_id}<br/>
                        <strong>Total Payable:</strong> ₹{result.total_amount_payable}<br/>
                        <strong>Monthly EMI:</strong> ₹{result.monthly_emi}
                    </p>
                </div>
            )}
        </div>
    );
}

export default Lend;
