import React, { useState } from 'react';
import './AccountOverview.css';

function AccountOverview() {
    const [customerId, setCustomerId] = useState('customer_001');
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');

    const API_URL = 'http://localhost:3001/api/v1';

    const handleFetch = async (e) => {
        e.preventDefault();
        setError('');
        setResult(null);
        if (!customerId) return;

        try {
            const response = await fetch(`${API_URL}/customers/${customerId}/overview`);
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to fetch overview.');
            setResult(data);
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="overview-container">
            <h2>Customer Account Overview</h2>
            <form onSubmit={handleFetch} className="form-container">
                <input type="text" value={customerId} onChange={(e) => setCustomerId(e.target.value)} placeholder="Enter Customer ID" required />
                <button type="submit">Get Overview</button>
            </form>
            {error && <p className="error">{error}</p>}
            {result && (
                <div className="result-container">
                    <h3>
                        Loan Overview for: {result.customer_name}
                    </h3>
                    <p className="customer-id-display">Customer ID: {result.customer_id} | Total Loans: {result.total_loans}</p>

                    {result.loans.length > 0 ? (
                        result.loans.map(loan => (
                            <div className="loan-card" key={loan.loan_id}>
                                <h4>Loan ID: {loan.loan_id}</h4>
                                <div className="summary-grid">
                                    <p><strong>Status:</strong> <span className={`status-${loan.status}`}>{loan.status.replace('_', ' ')}</span></p>
                                    <p><strong>Principal:</strong> ₹{parseFloat(loan.principal).toLocaleString('en-IN')}</p>
                                    <p><strong>Total Payable:</strong> ₹{parseFloat(loan.total_amount).toLocaleString('en-IN')}</p>
                                    <p><strong>Total Interest:</strong> ₹{parseFloat(loan.total_interest).toLocaleString('en-IN')}</p>
                                    <p><strong>Amount Paid:</strong> ₹{parseFloat(loan.amount_paid).toLocaleString('en-IN')}</p>
                                    <p><strong>EMI:</strong> ₹{parseFloat(loan.emi_amount).toLocaleString('en-IN')}</p>
                                    <p><strong>EMIs Left:</strong> {loan.emis_left}</p>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>This customer has no loans.</p>
                    )}
                </div>
            )}
        </div>
    );
}

export default AccountOverview;
