import React, { useState } from 'react';
import './AccountOverview.css';

const API_URL = 'http://localhost:3001/api/v1';

function AccountOverview() {
    const [customerId, setCustomerId] = useState('customer_001');
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');

    const handleFetch = async (e) => {
        e.preventDefault();
        setError('');
        setResult(null);
        if (!customerId) return;
        
        try {
            const response = await fetch(`${API_URL}/customers/${customerId}/overview`);
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to fetch overview.');
            setResult(data);
        } catch (err) {
            setError(err.message);
        }
    };

    const formatStatus = (status) => {
        if (!status) return '';
        return status.replace('_', ' ').toUpperCase();
    }

    return (
        <div className="overview-container">
            <h2>Customer Account Overview</h2>
            <form onSubmit={handleFetch} className="form-container">
                <input type="text" value={customerId} onChange={(e) => setCustomerId(e.target.value)} placeholder="Enter Customer ID" required />
                <button type="submit">Get Overview</button>
            </form>
            {error && <p className="error">{error}</p>}
            {result && (
                <div className="overview-results">
                    <h3 className="overview-customer-name">
                        Showing Overview for: {result.customer_name}
                    </h3>
                    <p className="total-loans-info">
                        Total Loans Taken: <strong>{result.total_loans}</strong>
                    </p>
                    <div className="loans-grid">
                        {result.loans.map(loan => (
                            <div key={loan.loan_id} className="loan-card">
                                <h4>Loan ID: {loan.loan_id}</h4>
                                <p><strong>Status:</strong> <span className={`status-${loan.status}`}>{formatStatus(loan.status)}</span></p>
                                <p><strong>Principal:</strong> ₹{parseFloat(loan.principal).toLocaleString('en-IN')}</p>
                                <p><strong>Total Payable:</strong> ₹{parseFloat(loan.total_amount).toLocaleString('en-IN')}</p>
                                <p><strong>Amount Paid Till Date:</strong> ₹{parseFloat(loan.amount_paid).toLocaleString('en-IN')}</p>
                                <p><strong>EMIs Left:</strong> {loan.emis_left}</p>
                                <p className="loan-created-date">
                                    <strong>Created On:</strong> {new Date(loan.created_at).toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' })}
                                </p>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
}

export default AccountOverview;
