import React, { useState } from 'react';
import './Ledger.css'; // Make sure to import the CSS

const API_URL = 'http://localhost:3001/api/v1';

function Ledger() {
    const [loanId, setLoanId] = useState('');
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');

    const handleFetch = async (e) => {
        e.preventDefault();
        setError('');
        setResult(null);
        if (!loanId) return;

        try {
            const response = await fetch(`${API_URL}/loans/${loanId}/ledger`);
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to fetch ledger.');
            setResult(data);
        } catch (err) {
            setError(err.message);
        }
    };

    // Helper to format status text
    const formatStatus = (status) => {
        if (!status) return '';
        return status.replace('_', ' ').toUpperCase();
    }

    return (
        <div className="ledger-container">
            <h2>View Loan Ledger</h2>
            <form onSubmit={handleFetch} className="form-container">
                <input 
                    type="text" 
                    value={loanId} 
                    onChange={(e) => setLoanId(e.target.value)} 
                    placeholder="Enter Loan ID" 
                    required 
                />
                <button type="submit">Get Ledger</button>
            </form>
            
            {error && <p className="error">{error}</p>}

            {result && (
                <div className="ledger-result-container">
                    <h3>Ledger for Loan: {result.loan_id}</h3>
                    
                    <div className="ledger-summary-grid">
                        <p><strong>Customer:</strong> {result.customer_name} ({result.customer_id})</p>
                        <p><strong>Status:</strong> <span className={`status-${result.status}`}>{formatStatus(result.status)}</span></p>
                        <p><strong>Principal:</strong> ₹{parseFloat(result.principal).toLocaleString('en-IN')}</p>
                        <p><strong>Total Payable:</strong> ₹{parseFloat(result.total_amount).toLocaleString('en-IN')}</p>
                        <p><strong>Amount Paid:</strong> ₹{parseFloat(result.amount_paid).toLocaleString('en-IN')}</p>
                        <p><strong>Balance:</strong> ₹{parseFloat(result.balance_amount).toLocaleString('en-IN')}</p>
                        <p><strong>Monthly EMI:</strong> ₹{parseFloat(result.monthly_emi).toLocaleString('en-IN')}</p>
                        <p><strong>EMIs Left:</strong> {result.emis_left}</p>
                    </div>

                    <h4>Transactions</h4>
                    <div className="table-wrapper">
                        <table className="transactions-table">
                            <thead>
                                <tr>
                                    <th>Transaction ID</th>
                                    <th>Date & Time</th>
                                    <th>Type</th>
                                    <th>Amount</th>
                                </tr>
                            </thead>
                            <tbody>
                                {result.transactions && result.transactions.length > 0 ? (
                                    result.transactions.map((tx) => (
                                        <tr key={tx.transaction_id}>
                                            <td data-label="Transaction ID">{tx.transaction_id}</td>
                                            <td data-label="Date & Time">{new Date(tx.date).toLocaleString('en-IN')}</td>
                                            <td data-label="Type">{tx.type}</td>
                                            <td data-label="Amount" className="amount">₹{parseFloat(tx.amount).toLocaleString('en-IN')}</td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr>
                                        <td colSpan="4">No transactions found for this loan.</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Ledger;
