import React, { useState } from 'react';
import './AccountOverview.css';

const API_URL = 'http://localhost:3001/api/v1';

function AccountOverview() {
    const [customerId, setCustomerId] = useState('customer_001'); // Default dummy customer
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');

    const handleFetch = async (e) => {
        e.preventDefault();
        setError('');
        setResult(null);
        if (!customerId) return;

        try {
            const response = await fetch(`${API_URL}/customers/${customerId}/overview`);
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to fetch overview.');
            setResult(data);
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="overview-container">
            <h2>Customer Account Overview</h2>
            <form onSubmit={handleFetch} className="form-container">
                <input
                    type="text"
                    value={customerId}
                    onChange={(e) => setCustomerId(e.target.value)}
                    placeholder="Enter Customer ID"
                    required
                />
                <button type="submit">Get Overview</button>
            </form>
            {error && <p className="error">{error}</p>}
            {result && (
                <div className="result">
                    <h3>Overview for Customer: {result.customer_id}</h3>
                    <p><strong>Total Loans:</strong> {result.total_loans}</p>
                    <hr />
                    {result.loans.map((loan) => (
                        <div key={loan.loan_id} className="loan-summary">
                            <h4>Loan ID: {loan.loan_id}</h4>
                            <p><strong>Principal:</strong> ₹{loan.principal}</p>
                            <p><strong>Total Amount:</strong> ₹{loan.total_amount}</p>
                            <p><strong>Total Interest:</strong> ₹{loan.total_interest}</p>
                            <p><strong>EMI Amount:</strong> ₹{loan.emi_amount}</p>
                            <p><strong>Amount Paid:</strong> ₹{loan.amount_paid}</p>
                            <p><strong>EMIs Left:</strong> {loan.emis_left}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default AccountOverview;
