import React, { useState } from 'react';
import './Lend.css';
import Modal from '../Modal/Modal'; // Import the Modal component

const API_URL = 'http://localhost:3001/api/v1';

function Lend() {
    const [formData, setFormData] = useState({
        customer_id: '',
        loan_amount: '',
        loan_period_years: '',
        interest_rate_yearly: ''
    });
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    // State for managing the modal
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalInfo, setModalInfo] = useState({ title: '', message: '', details: [] });
    const [copyButtonText, setCopyButtonText] = useState('Copy');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleShowModal = (title, message, details = []) => {
        setModalInfo({ title, message, details });
        setIsModalOpen(true);
    };

    const handleCopy = (text) => {
        // Use the modern navigator.clipboard API
        navigator.clipboard.writeText(text).then(() => {
            setCopyButtonText('Copied!');
            setTimeout(() => {
                setCopyButtonText('Copy');
            }, 2000); // Reset button text after 2 seconds
        }, (err) => {
            console.error('Failed to copy text: ', err);
            setCopyButtonText('Failed');
        });
    };
    
    // Custom close handler to reset the copy button text when the modal is closed
    const closeModalAndReset = () => {
        setIsModalOpen(false);
        setCopyButtonText('Copy');
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);
        try {
            const response = await fetch(`${API_URL}/loans`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });
            const data = await response.json();
            if (!response.ok) throw new Error(data.error || 'Failed to create loan.');
            
            handleShowModal(
                "Loan Created Successfully",
                `A new loan has been originated for ${data.customer_name}.`,
                [
                    { label: "Loan ID", value: data.loan_id },
                    { label: "Total Payable", value: `₹${data.total_amount_payable}` },
                    { label: "Monthly EMI", value: `₹${data.monthly_emi}` }
                ]
            );

            setFormData({
                customer_id: '',
                loan_amount: '',
                loan_period_years: '',
                interest_rate_yearly: ''
            });

        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <>
            <Modal 
                isOpen={isModalOpen} 
                onClose={closeModalAndReset} 
                title={modalInfo.title}
            >
                <p>{modalInfo.message}</p>
                {modalInfo.details.length > 0 && (
                    <div className="modal-details">
                        {modalInfo.details.map(detail => (
                            <div key={detail.label} className="modal-detail-item">
                                <span className="modal-detail-label">{detail.label}:</span>
                                {detail.label === "Loan ID" ? (
                                    <div className="modal-value-container">
                                        <span className="modal-detail-value">{detail.value}</span>
                                        <button 
                                            className="copy-button" 
                                            onClick={() => handleCopy(detail.value)}
                                        >
                                            {copyButtonText}
                                        </button>
                                    </div>
                                ) : (
                                    <span className="modal-detail-value">{detail.value}</span>
                                )}
                            </div>
                        ))}
                    </div>
                )}
            </Modal>

            <h2 className="component-title">Create a New Loan</h2>
            <p className="component-subtitle">Issue a new loan to an existing customer.</p>

            <form onSubmit={handleSubmit} className="form-container">
                {/* Form inputs... */}
                <input 
                    type="text" 
                    name="customer_id" 
                    value={formData.customer_id} 
                    onChange={handleChange} 
                    placeholder="Enter Customer ID" 
                    required 
                    disabled={isLoading}
                />
                <input 
                    type="number" 
                    name="loan_amount" 
                    value={formData.loan_amount} 
                    onChange={handleChange} 
                    placeholder="Loan Amount (₹)" 
                    required 
                    disabled={isLoading}
                />
                <input 
                    type="number" 
                    name="loan_period_years" 
                    value={formData.loan_period_years} 
                    onChange={handleChange} 
                    placeholder="Loan Period (Years)" 
                    required 
                    disabled={isLoading}
                />
                <input 
                    type="number" 
                    name="interest_rate_yearly" 
                    value={formData.interest_rate_yearly} 
                    onChange={handleChange} 
                    placeholder="Interest Rate (% Yearly)" 
                    required 
                    disabled={isLoading}
                />
                <button type="submit" disabled={isLoading}>
                    {isLoading ? 'Creating...' : 'Create Loan'}
                </button>
            </form>

            {error && <p className="error">{error}</p>}
        </>
    );
}

export default Lend;
